export const Model = (props) => {
    return (
      <div id="model" className='text-center' style={{padding: "40px 0px 0px 0px"}}>
         <div className='col-md-10 col-md-offset-1 section-title'>
          <h2>Model</h2>
          <br/>
          <p><b>Momentum Investing</b> is an investment strategy that involves buying securities that have had strong recent price performance and selling those that have had weak performance. The idea behind this strategy is that securities that have recently performed well are likely to continue to do so, while those that have performed poorly are likely to continue to underperform. This strategy is based on the concept of momentum, which is the <b>tendency of an asset's price to continue moving</b> in the same direction it has been moving in the recent past.</p>
        </div>
      </div>
    );
  };
